//Primero creo la clase molde y establezo las propiedades 
export class BankAccount {
    private owner: string;
    private balance: number;
    private accountType: string; //ej cuenta corriente


    //creo el constructor metodo por el cual voy a crear instancias
   public constructor(owner: string, accountType: string) {
        this.owner = owner;
        this.balance = 0;
        this.accountType = accountType;
    }

    //metodo 1. deposito
    deposit(amount: number): void {
        if (amount > 0) {
            this.balance += amount;
    } }

    //metodo 2. retiro
    withdraw(amount: number): void {
        if (amount > 0 && amount <= this.balance) {
            this.balance -= amount;
         
    } }

    getBalance(): number {
        return this.balance;
    }

}



